    income < incomeLevel14
  ) {
    taxBracket = tax5;
  } else if (
    taxCat === "married filing jointly" &&
    income > incomeLevel14 &&
    income < incomeLevel16
  ) {
    taxBracket = tax6;
  } else if (taxCat === "married filing jointly" && income > incomeLevel16) {
    taxBracket = tax7;
  } else if (taxCat === "head of household" && income < incomeLevel2) {
    taxBracket = tax1;
  } else if (
    taxCat === "head of household" &&
    income > incomeLevel2 &&
    income < incomeLevel5
  ) {
    taxBracket = tax2;
  } else if (
    taxCat === "head of household" &&
    income > incomeLevel5 &&
    income < incomeLevel7
  ) {
    taxBracket = tax3;
  } else if (
    taxCat === "head of household" &&
    income > incomeLevel7 &&
    income < incomeLevel9
  ) {
    taxBracket = tax4;
  } else if (
    taxCat === "head of household" &&
    income > incomeLevel9 &&
    income < incomeLevel12
  ) {
    taxBracket = tax5;
  } else if (
    taxCat === "head of household" &&
    income > incomeLevel12 &&
    income < incomeLevel15
