const calcInvest = () => {
  const savingsInvest =
    (savingsValue + savingsCont) * ((1 + savingsRate / 100) ^ (fireAge - age));
  const otherInvest =
    (otherValue + otherCont) * ((1 + otherRate / 100) ^ (fireAge - age));
  const equation = savingsInvest + otherInvest;
  invest = equation;
};

const calcFire = () => {
  const equation = compoundIncome - compoundExpenses + invest;
  fire = equation;
};

const findAge = () => {
  while (fire > 25 * expenses) {
    fireAge = fireAge - 1;
    calcCompoundIncome();
    calcCompoundExpenses();
    calcInvest();
    calcFire();
  }
  setFreeAge(fireAge + 1);
};

